public IneqMesh GetIneqMesh()
{
  	var sdf = new SignedDistance("box.stl");
	var b = (# sdf.From(x, y, z)) ;
  
    var res = new IneqMesh
    {
        X0 = -1.2454,
        Y0 = -1.213213,
        Z0 = -1.21564,
        X1 = 1.2146565,
        Y1 = 1.23651,
        Z1 = 1.21165,
        D = 0.2,
        Boxed = false,
        IneqTree =
          b //| Ball(0,-0.5,-0.3,0.5)
	};
  
  	res.PrepareBackgroundMeshBeforeApriory = () => 
    {
		sdf.ForceSharpEdges(res, b, 10);
    };
  
  	return res;
}
