public class SmoothSDFWrapper
{
    // The original exact SDF function
    private Func<double, double, double, double> _originalSDF;

    public SmoothSDFWrapper(Func<double, double, double, double> originalSDF)
    {
        _originalSDF = originalSDF;
    }

    /// <param name="x, y, z">Target coordinates</param>
    /// <param name="lambda">Smoothing strength (typically 0.1 to 0.5)</param>
    /// <param name="h">Sampling step (should be roughly your target mesh edge length)</param>
    public double Eval(double x, double y, double z, double lambda = 0.001d, double h = 0.01d)
    {
        double center = _originalSDF(x, y, z);

        // Sample 6 neighbors
        double valXp = _originalSDF(x + h, y, z);
        double valXm = _originalSDF(x - h, y, z);
        double valYp = _originalSDF(x, y + h, z);
        double valYm = _originalSDF(x, y - h, z);
        double valZp = _originalSDF(x, y, z + h);
        double valZm = _originalSDF(x, y, z - h);

        // Discrete Laplacian approximation
        double laplacian = (valXp + valXm + valYp + valYm + valZp + valZm - 6.0 * center) / (h * h);

        // Return the diffused value
        return center + lambda * laplacian + 0.03;
    }
}

