static double SmoothSDF(double d, double eps = 0.1, double offset = 0.02)
{
	//return d/sqrt(1 + d*d/(eps*eps));
  	return eps * Math.Tanh((d + offset) / eps);
}


public IneqMesh GetIneqMesh()
{
   	var sdf = new SignedDistance("letters\\M.stl");
  	var sm = new SmoothSDFWrapper(sdf.From);
	var f = (double x, double y,double z) => sm.Eval(x, y, z);
  	var b = new IneqTree(f) ;
  
    var res = new IneqMesh
    {
        X0 = -1,
        Y0 = -1,
        Z0 = -1,
        X1 = 1,
        Y1 = 1,
        Z1 = 1,
        D = 0.1d,
        Boxed = false,
        IneqTree =
          b //| (# z+0.9)
	};
  
  	//res.ProjectToSurfaceSpec[f] = sdf.Project;
  	res.PrepareBackgroundMeshBeforeApriory = () => res.RefineTetrahedralMesh(0, 2, 0.02);
  
  	return res;
}
