public IneqTree TorusPQ(double x0, double y0, double z0, double r, double R)
{

    return new IneqTree((x, y, z) =>
    {
		double p, q;

      	x = x - x0;
        y = y - y0;
        z = z - z0;      

      	p = z < 0 ? 2 : 8;
      	q = 10;
      
      	double pn = pow(pow(abs(x), p) + pow(abs(z), p), 1.0d/p);
      
      	double sx =  R * x / pn;
      	double sz =  R * z / pn;

		double v = sqrt((x-sx)*(x-sx) + (z-sz)*(z-sz));
      
      	return pow(pow(y, q) + pow(2*v, q), 1.0/q) - r;

    });
}

public IneqTree Balls(int count, double r/*, out List<Point> points*/)
{
    IneqTree res = new IneqTree();
  	//points = new List<Point>();
	for (int j = -1; j < 2; j++)
    for (int i = 0; i < count; i++)
    {
		double x = r*cos(i * 2 * PI / count + abs(j) * PI / count);
      	double y = r*sin(i * 2 * PI / count + abs(j) * PI / count);
      
		var b = Ball(x,y,0.75 * j,0.2);
      	res = res | b;
    }

    return res;
}

public  IneqTree Mug()
{
  	var c1 = Cylinder(0, 0 ,-1, 0, 0 ,1, 0.7, 1) | Balls(6, 0.55);
  	var c2 = Cylinder(0, 0 ,-0.9, 0, 0 ,1.1, 0.6, 1);
  
  	c1.Transform(delegate(ref double x, ref double y, ref double z) 
	{
		double s = 1+(cos(z) - cos(1.0))/2;      
      	x /= s; y /= s; 
    });

  	c2.Transform(delegate(ref double x, ref double y, ref double z) 
	{
		double s = 1+(cos(z) - cos(1.0))/2;      
      	x /= s; y /= s; 
    });
  
  	var res = (TorusPQ(0.65, 0, 0, 0.15, 0.6) | c1) & !c2;

	return res;	  
}

public IneqMesh GetIneqMesh()
{
    var res = new IneqMesh
    {
        X0 = -1.1,
        Y0 = -1.1,
        Z0 = -1.1,
        X1 = 1.9,
        Y1 = 1.1,
        Z1 = 1.1,
        D = 0.1d,
        Boxed = false,
        IneqTree =  Mug()
      };
  
	res.PrepareBackgroundMeshBeforeApriory = () => 
    {
        for (int i = 0; i < 3; i++)
        {
      		res.RefineTetrahedralMesh(0, 2, 0.05, false);
      		res.JiggleBackgroundMash(3);
        }     
    };
  
	return res;  
}
