[advanced]
public IneqMesh GetIneqMesh()
{
    return new IneqMesh
    {
        X0 = -1.0,
        Y0 = -1.0,
        Z0 = -1.0,
        X1 = 1.0,
        Y1 = 1.0,
        Z1 = 1.0,
        D = 0.04d,
        Boxed = true,
        IneqTree = (
          PrismTorus(0.15, 0.7, 5, af: (a,aa) => a + 0.5 * Math.Sin(12 * aa)) |
          PrismTorus(0.15, 0.7, 5, sa:PI/5,  af: (a,aa) => a + 0.5 * Math.Sin(12 * aa)) )
          & !(# x*x + y*y -0.1)
      };
}
