public static IneqTree TorusPQ(double x0, double y0, double z0, double r, double R)
{

    return new IneqTree((x, y, z) =>
    {
		double p, q;

      	x = x - x0;
        y = y - y0;
        z = z - z0;      

      	p = x < 0 ? 2 : 8;
      	q = 10;
      
      	double pn = pow(pow(abs(x), p) + pow(abs(y), p), 1.0d/p);
      
      	double sx =  R * x / pn;
      	double sy =  R * y / pn;

		double v = sqrt((x-sx)*(x-sx) + (y-sy)*(y-sy));
      
      	return pow(pow(z, q) + pow(2*v, q), 1.0/q) - r;

    });
}
public IneqMesh GetIneqMesh()
{
    return new IneqMesh
    {
        X0 = -1.1,
        Y0 = -1.1,
        Z0 = -1.1,
        X1 = 1.1,
        Y1 = 1.1,
        Z1 = 1.1,
        D = 0.1d,
        Boxed = true,
        IneqTree = TorusPQ(0, 0, 0, 0.2, 0.7)
      };
}
